import React, {useEffect, useState} from 'react';
import {
  randomIntFromInterval,
  useInterval,
} from '../../utils/utils';

import './Board.css';

class LinkedListNode {
  constructor(value) {
    this.value = value;
    this.next = null;
  }
}

class LinkedList {
  constructor(value) {
    const node = new LinkedListNode(value);
    this.head = node;
    this.tail = node;
  }
}

const Direction = {
  UP: 'UP',
  RIGHT: 'RIGHT',
  DOWN: 'DOWN',
  LEFT: 'LEFT',
};


const BOARD_SIZE = 20;

const getStartingSnakeLLValue = board => {
  const rowSize = board.length;
  const colSize = board[0].length;
  const startingRow = Math.round(rowSize / 3);
  const startingCol = Math.round(colSize / 3);
  const startingCell = board[startingRow][startingCol];
  return {
    row: startingRow,
    col: startingCol,
    cell: startingCell,
  };
};

const Board = () => {
  const [begin, setBegin] = useState(true)
  const [score, setScore] = useState(0);
  const [chance, setChance] = useState(0.8)
  const [board, setBoard] = useState(createBoard(BOARD_SIZE));
  const [snake, setSnake] = useState(
    new LinkedList(getStartingSnakeLLValue(board)),
  );
  const [snakeCells, setSnakeCells] = useState(
    new Set([snake.head.value.cell]),
  );
  const [foodCell, setFoodCell] = useState(snake.head.value.cell + 5);
  const [direction, setDirection] = useState(Direction.RIGHT);
  const [speed, setSpeed] = useState(150)
  const [menu, setMenu] = useState(false)
  const [speedIncreases, setSpeedIncreases] = useState(0);
  useEffect(() => {
    window.addEventListener('keydown', e => {
      if (!menu) {
        handleKeydown(e);
      }
    });
  }, []);
  useEffect(() => {
    if (score >= 50 * (speedIncreases + 1)) {
      setSpeedIncreases((prev) => prev + 1);
      setSpeed((prev) => {
        if(prev > 50) {
          return prev - 20
        }
      });
    }
    if (score == 0) {
      setSpeed(150)
      setSpeedIncreases(0)
    }
  }, [score, speedIncreases]);
  useInterval(() => {
    if (!menu) {
      moveSnake();
    }
  }, speed);

  const handleKeydown = e => {
    const newDirection = getDirectionFromKey(e.key)
    const isValidDirection = newDirection !== '';
    if (!isValidDirection) return;
    const snakeWillRunIntoItself =
      getOppositeDirection(newDirection) === direction && snakeCells.size > 1;

    if (snakeWillRunIntoItself) return;
    setDirection(newDirection);
  };

  const moveSnake = () => {
    if(begin) {
      const currentHeadCoords = {
        row: snake.head.value.row,
        col: snake.head.value.col,
      };
  
      const nextHeadCoords = getCoordsInDirection(currentHeadCoords, direction);
      if (isOutOfBounds(nextHeadCoords, board)) {
        handleGameOver();
        return;
      }
      const nextHeadCell = board[nextHeadCoords.row][nextHeadCoords.col];
      if (snakeCells.has(nextHeadCell)) {
        handleGameOver();
        return;
      }
  
      const newHead = new LinkedListNode({
        row: nextHeadCoords.row,
        col: nextHeadCoords.col,
        cell: nextHeadCell,
      });
      const currentHead = snake.head;
      snake.head = newHead;
      currentHead.next = newHead;
  
      const newSnakeCells = new Set(snakeCells);
      newSnakeCells.delete(snake.tail.value.cell);
      newSnakeCells.add(nextHeadCell);
  
      snake.tail = snake.tail.next;
      if (snake.tail === null) snake.tail = snake.head;
  
      const foodConsumed = nextHeadCell === foodCell;
      if (foodConsumed) {
        // This function mutates newSnakeCells.
        growSnake(newSnakeCells);
        handleFoodConsumption(newSnakeCells);
      }
  
      setSnakeCells(newSnakeCells);
    }
  };

  // This function mutates newSnakeCells.
  const growSnake = newSnakeCells => {
    const growthNodeCoords = getGrowthNodeCoords(snake.tail, direction);
    if (isOutOfBounds(growthNodeCoords, board)) {
      // Snake is positioned such that it can't grow; don't do anything.
      return;
    }
    const newTailCell = board[growthNodeCoords.row][growthNodeCoords.col];
    const newTail = new LinkedListNode({
      row: growthNodeCoords.row,
      col: growthNodeCoords.col,
      cell: newTailCell,
    });
    const currentTail = snake.tail;
    snake.tail = newTail;
    snake.tail.next = currentTail;

    newSnakeCells.add(newTailCell);
  };


  const handleFoodConsumption = newSnakeCells => {
    const maxPossibleCellValue = BOARD_SIZE * BOARD_SIZE;
    let nextFoodCell;

    while (true) {
      nextFoodCell = randomIntFromInterval(1, maxPossibleCellValue);
      if (newSnakeCells.has(nextFoodCell) || foodCell === nextFoodCell)
        continue;
      break;
    }

    setFoodCell(nextFoodCell);
    setChance(Math.random())
    if (chance > 0.05 && chance < 0.2) {
      setScore(score + 5);
    }
    else if (chance < 0.05) {
      setScore(score + 10);
    }
    else {
      setScore(score + 1);
    }

  };

  const handleGameOver = () => {
    setMenu(true)
  };

  const resumeGame = () => {
    setMenu(false)
    setScore(0)
    const snakeLLStartingValue = getStartingSnakeLLValue(board);
    setSnake(new LinkedList(snakeLLStartingValue));
    setFoodCell(snakeLLStartingValue.cell + 5);
    setSnakeCells(new Set([snakeLLStartingValue.cell]));
    setDirection(Direction.RIGHT);
  }
  const beginGame = () => {
    setBegin(false)
  }
  return (
    <>
      {!menu && <h1>Score: {score}</h1>}
      <div className="board">
        {!begin ? (
          board.map((row, rowIdx) => (
            <div key={rowIdx} className="row">
              {row.map((cellValue, cellIdx) => {
                const className = getCellClassName(
                  cellValue,
                  foodCell,
                  snakeCells,
                  score,
                  chance
                );
                return <div key={cellIdx} className={className}></div>;
              })}
            </div>
          ))
        ) : (
          <div className='begin-menu'>
          <div className='begin-menu-score'>
            <input placeholder='Enter your name' />
            <button className='begin-menu-score-btn' onClick={beginGame}>Try again</button>
          </div>
        </div>
        )}
        {menu && (
        <div className='board-menu'>
          <div className='board-menu-score'>
            <h1 className='board-menu-score-title'>Your Result: {score}</h1>
            <button className='board-menu-score-btn' onClick={resumeGame}>Try again</button>
          </div>
        </div>
      )}
      </div>
    </>
  );
};

const createBoard = BOARD_SIZE => {
  let counter = 1;
  const board = [];
  for (let row = 0; row < BOARD_SIZE; row++) {
    const currentRow = [];
    for (let col = 0; col < BOARD_SIZE; col++) {
      currentRow.push(counter++);
    }
    board.push(currentRow);
  }
  return board;
};

const getCoordsInDirection = (coords, direction) => {
  if (direction === Direction.UP) {
    return {
      row: coords.row - 1,
      col: coords.col,
    };
  }
  if (direction === Direction.RIGHT) {
    return {
      row: coords.row,
      col: coords.col + 1,
    };
  }
  if (direction === Direction.DOWN) {
    return {
      row: coords.row + 1,
      col: coords.col,
    };
  }
  if (direction === Direction.LEFT) {
    return {
      row: coords.row,
      col: coords.col - 1,
    };
  }
};

const isOutOfBounds = (coords, board) => {
  const {row, col} = coords;
  if (row < 0 || col < 0) return true;
  if (row >= board.length || col >= board[0].length) return true;
  return false;
};

const getDirectionFromKey = key => {
  if (key === 'ArrowUp') return Direction.UP;
  if (key === 'ArrowRight') return Direction.RIGHT;
  if (key === 'ArrowDown') return Direction.DOWN;
  if (key === 'ArrowLeft') return Direction.LEFT;
  return '';
};

const getNextNodeDirection = (node, currentDirection) => {
  if (node.next === null) return currentDirection;
  const {row: currentRow, col: currentCol} = node.value;
  const {row: nextRow, col: nextCol} = node.next.value;
  if (nextRow === currentRow && nextCol === currentCol + 1) {
    return Direction.RIGHT;
  }
  if (nextRow === currentRow && nextCol === currentCol - 1) {
    return Direction.LEFT;
  }
  if (nextCol === currentCol && nextRow === currentRow + 1) {
    return Direction.DOWN;
  }
  if (nextCol === currentCol && nextRow === currentRow - 1) {
    return Direction.UP;
  }
  return '';
};

const getGrowthNodeCoords = (snakeTail, currentDirection) => {
  const tailNextNodeDirection = getNextNodeDirection(
    snakeTail,
    currentDirection,
  );
  const growthDirection = getOppositeDirection(tailNextNodeDirection);
  const currentTailCoords = {
    row: snakeTail.value.row,
    col: snakeTail.value.col,
  };
  const growthNodeCoords = getCoordsInDirection(
    currentTailCoords,
    growthDirection,
  );
  return growthNodeCoords;
};

const getOppositeDirection = direction => {
  if (direction === Direction.UP) return Direction.DOWN
  if (direction === Direction.RIGHT) return Direction.LEFT;
  if (direction === Direction.DOWN) return Direction.UP;
  if (direction === Direction.LEFT) return Direction.RIGHT;
};

const getCellClassName = (
  cellValue,
  foodCell,
  snakeCells,
  score,
  chance
) => {
  let className = 'cell';
  if (cellValue === foodCell) {
    if (chance > 0.05 && chance < 0.2) {
      className = 'cell cell-purple';
    }
    else if (chance < 0.05) {
      className = 'cell cell-blue';
    }
    else {
      className = 'cell cell-red';
    }
  
  }
  if (snakeCells.has(cellValue)) className = 'cell cell-green';

  return className;
};

export default Board;
