import React, { useState } from "react";
import "./Board.css";

const boardSize = 10;

function linkedListNode(value) {
  return {
    value,
    next: null,
  };
}

function singlyLinkedList(value) {
  const node = linkedListNode(value);
  return {
    head: node,
    tail: node,
  };
}

const Direction = {
  UP: 'UP',
  RIGHT: 'RIGHT',
  DOWN: 'DOWN',
  LEFT: 'LEFT',
};

const Board = () => {
  const [board, setBoard] = useState(createBoard(boardSize));
  const [snakeCells, setSnakeCells] = useState([44]);
  const [snake, setSnake] = useState(singlyLinkedList(44));

  console.log(board);
  return (
    <div className="board">
      {board.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cellValue, cellIndex) => (
            <div
              key={cellIndex}
              className={`cell ${
                snakeCells.includes(cellValue) ? "cell-green" : ""
              }`}
            ></div>
          ))}
        </div>
      ))}
    </div>
  );
};

const createBoard = (BOARD_SIZE) => {
  let counter = 1;
  const board = [];
  for (let row = 0; row < BOARD_SIZE; row++) {
    const currentRow = [];
    for (let col = 0; col < BOARD_SIZE; col++) {
      currentRow.push(counter++);
    }
    board.push(currentRow);
  }
  return board;
};

export default Board;
